import axios from "axios";
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { Navbar } from "react-bootstrap";

const EditPet = (props) => {
    const { id } = useParams();
    const [name, setName] =useState("");
    const [colors, setColors] = useState("");
    const [wins, setWins] = useState("");
    const [losses, setLosses] = useState("");
    const [errors, setErrors] = useState({});
    const navigate = useNavigate();

    useEffect(() => {
        axios
            .get(`http://localhost:8000/api/deck/${id}`)
                .then((res) => {
                    const { data } = res;
                    setName(data.name);
                    setColors(data.colors);
                    setWins(data.wins);
                    setLosses(data.losses);
                })
            .catch((err) => {
                console.log(err.response);
            });
            }, []);

    const updateHandler = (e) => {
        e.preventDefault();

        axios
            .put(`http://localhost:8000/api/deck/${id}`, { name, colors, wins, losses })
                .then((response) => {
                    console.log(response);
                    navigate("/");
                })
            .catch((err) => {
                console.log(err.response.data.err.errors);
                setErrors(err.response.data.err.errors);
            });
        };

        return (
            <div className="container">
                <div className="row">
                <Navbar style={{justifyContent:"space-between"}}>
                    <h1>Wizard of Odds</h1>
                    <Link to="/">back to home</Link>
                </Navbar>
                    <h3>Update {name}</h3>
                    <form onSubmit={updateHandler}>
                    <div className="row">
                        <div className="col-4">
                        <div className="form-group">
                            <label htmlFor="name">Deck Name:</label>
                            <input
                                type="text"
                                className="form-control"
                                onChange={(e) => setName(e.target.value)}
                                value={name}
                            />
                            {errors.name ? <p className="text-danger">{errors.name.message}</p> : null}
                        </div>
                        <br></br>
                        <div className="form-group">
                            <label htmlFor="name">Colors:</label>
                            <input
                                type="text"
                                className="form-control"
                                onChange={(e) => setColors(e.target.value)}
                                value={colors}
                            />
                            {errors.colors ? <p className="text-danger">{errors.colors.message}</p> : null}
                        </div>
                        <br></br>
                        <div>
                        <p>Magic: The Gathering has five colors: White, Blue, Black, Red, and Green. Please enter the colors that make up your deck. If your deck has all five colors, type "All". If your deck has no colors, type "Colorless". To choose multiple colors, seperate them with a "/". </p>
                        </div>
                    </div>
                    </div>
                        <button className="btn btn-primary" type="submit">
                            Update Deck
                        </button>
                    </form>
                    </div>
                </div>
            
        );
    };
    
    export default EditPet;
