import {useEffect, useState } from "react";
import axios from "axios";


const Calculator = () => {
    const [allDecks, setAllDecks] = useState([]);
    const [deck1, setDeck1] = useState([]);
    const [deck2, setDeck2] = useState([]);
    useEffect(()=> {
        axios
        .get("http://localhost:8000/api/deck")
            .then((res)=> {
                console.log(res.data);
                setAllDecks(res.data);
            })
            .catch((err)=> {
                console.log(err);
            });
    }, []);

    const calculate = (e) => {
        var deck1w = document.getElementById("deck1").value;
        var deck1l = document.getElementById("deck1").loss;
        var total = deck1w +deck1l;
        console.log(deck1w);
        console.log(deck1l);
        console.log(total);
    }


    return (
        <div>
        <label htmlFor="deck1">Choose Player 1's Deck:</label>
        <select onChange={(e) => setDeck1(e.target.value)} name="deck1" id="deck1">
            {allDecks.map((deck, index)=>{
                return (
                    <option value={deck.wins} data-loss={deck.losses} key={deck._id}>{deck.name}: {deck.wins}-{deck.losses}</option>
                )
            })}
        </select>
        <label htmlFor="deck2">Choose Player 2's Deck:</label>
        <select name="deck2" id="deck2">
            {allDecks.map((deck, index)=>{
                return (
                    <option key={deck._id}>{deck.name}: {deck.wins}-{deck.losses}</option>
                )
            })}
        </select>
        <button className="btn btn-primary" onClick={()=>calculate()}>
                            Calculate
                        </button>
        </div>
    );
};

export default Calculator;
