import {useEffect, useState} from "react";
import axios from "axios";
import {Link} from "react-router-dom";
import { Navbar} from 'react-bootstrap';

const DisplayAll = () => {
    const [allDecks, setAllDecks] = useState([]);
    useEffect(()=> {
        axios
        .get("http://localhost:8000/api/deck")
            .then((res)=> {
                console.log(res.data);
                setAllDecks(res.data);
            })
            .catch((err)=> {
                console.log(err);
            });
    }, []);

    return (
        <div className="container">
            <div className="row justify-content-center">
            <Navbar style={{justifyContent:"space-evenly"}}>
                <h1>Wizard of Odds</h1>
                <Link to="/new">
                    <button className="btn btn-primary">Add a new Deck</button>
                </Link>
            </Navbar>
                    <table className="table table-bordered table-striped table-hover w-auto">
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Colors</th>
                                <th>Wins</th>
                                <th>Losses</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {allDecks.map((deck, index) => {
                                return (
                                    <tr key={deck._id}>
                                        <td>{deck.name}</td>
                                        <td>{deck.colors}</td>
                                        <td>{deck.wins}</td>
                                        <td>{deck.losses}</td>
                                        <td className="table-dark">
                                            <Link to={`/details/${deck._id}`}>
                                                <button className="btn btn-success mr-1">Details</button>
                                            </Link>
                                            <Link to={`/edit/${deck._id}`}>
                                                <button className="btn btn-warning">Edit</button>
                                            </Link>
                                        </td>
                                    </tr>
                                );
                            })}
                        </tbody>
                    </table>
            </div>
        </div>
    );
};

export default DisplayAll;
