import {useEffect, useState } from "react";
import axios from "axios";


const Calculator = () => {
    const [allDecks, setAllDecks] = useState([]);
    const [deck1, setDeck1] = useState([]);
    const [deck2, setDeck2] = useState([]);
    useEffect(()=> {
        axios
        .get("http://localhost:8000/api/deck")
            .then((res)=> {
                console.log(res.data);
                setAllDecks(res.data);
            })
            .catch((err)=> {
                console.log(err);
            });
    }, []);

    const calculate = (e) => {
        var deck1 = document.getElementById("deck1").value;
        var deck2 = document.getElementById("deck2").value;
        console.log(deck1);
        console.log(deck1);
            axios
            .get(`http://localhost:8000/api/deck/${deck1}`)
                .then((res)=>{
                    const {data} = res;
                    setDeck1(data);
                    var totalGames =(data.wins+data.losses);
                    var winChance1 = Math.round((data.wins/totalGames)*100);
                    document.getElementById("output").innerHTML = winningState;

                    console.log(winChance1);

                })
                .catch((err)=>{
                    console.log(err);
                })
                axios
                .get(`http://localhost:8000/api/deck/${deck2}`)
                    .then((res)=>{
                        const {data} = res;
                        setDeck2(data);
                        var totalGames =(data.wins+data.losses);
                        var winChance2 = Math.round((data.wins/totalGames)*100);
                        var fullWinChance = winChance1/winChance2;
                        var winningState = "The odds for this match are" + winChance2 + ":" + winChance2 + ".";
                        document.getElementById("output").innerHTML = winningState;
    
                        console.log(fullWinChance);

    
                    })
                    .catch((err)=>{
                        console.log(err);
                    })
            
        }
    


    return (
        <div>
        <label htmlFor="deck1">Choose Player 1's Deck:</label>
        <select onChange={(e) => setDeck1(e.target.value)} name="deck1" id="deck1">
            {allDecks.map((deck, index)=>{
                return (
                    <option value={deck._id} key={deck._id}>{deck.name}: {deck.wins}-{deck.losses}</option>
                    )
            })}
        </select>
        <label htmlFor="deck2">Choose Player 2's Deck:</label>
        <select name="deck2" id="deck2">
            {allDecks.map((deck, index)=>{
                return (
                    <option value={deck._id} key={deck._id}>{deck.name}: {deck.wins}-{deck.losses}</option>
                )
            })}
        </select>
        <button className="btn btn-primary" onClick={()=>calculate()}>
                            Calculate
                        </button>
        <p id="output"></p>
        </div>
    );
};

export default Calculator;
