import {useEffect, useState, useNavigate} from "react";
import axios from "axios";
import {Link} from "react-router-dom";
import { Navbar} from 'react-bootstrap';

const DisplayAll = () => {
    const [allDecks, setAllDecks] = useState([]);
    useEffect(()=> {
        axios
        .get("http://localhost:8000/api/deck")
            .then((res)=> {
                console.log(res.data);
                setAllDecks(res.data);
            })
            .catch((err)=> {
                console.log(err);
            });
    }, []);

    const navigate = useNavigate();

    const deleteHandler = (id) => {
        axios
        .delete(`http://localhost:8000/api/deck/${id}`)
            .then((res) => {
                navigate('/');
            })
            .catch((err) => {
                console.log(err);
            });
    };

    return (
        <div className="container">
            <div className="row justify-content-center">
            <Navbar style={{justifyContent:"space-evenly"}}>
                <h1>Wizard of Odds</h1>
                <Link to="/new">
                    <button className="btn btn-primary">+New Deck</button>
                </Link>
                <Link to ="/calculate">
                    <button className="btn btn-secondary">Calculate Odds!</button>
                </Link>
            </Navbar>
                    <h4 className="text-center">Here's the competition!</h4>
                    <table className="table table-bordered table-striped table-hover w-auto">
                        <thead className="text-center">
                            <tr>
                                <th>Deck Name</th>
                                <th>Colors</th>
                                <th>Wins</th>
                                <th>Losses</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {allDecks.map((deck, index) => {
                                return (
                                    <tr key={deck._id}>
                                        <td>{deck.name}</td>
                                        <td>{deck.colors}</td>
                                        <td>{deck.wins}</td>
                                        <td>{deck.losses}</td>
                                        <td className="table-dark">
                                            <button className="btn btn-danger text-white" onClick={()=>deleteHandler(deck._id)}>-Delete</button>

                                            <Link to={`/edit/${deck._id}`}>
                                                <button className="btn btn-info">Edit Info</button>
                                            </Link>
                                        </td>
                                    </tr>
                                );
                            })}
                        </tbody>
                    </table>
            </div>
        </div>
    );
};

export default DisplayAll;
