import axios from "axios";
import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Navbar } from "react-bootstrap";

const DeckForm = () => {
    const [name, setName] = useState("");
    const [colors, setColors] = useState("");
    const [errors, setErrors] = useState("");
    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();
            axios
                .post("http://localhost:8000/api/deck", { name, colors })
                    .then((response) => {
                        console.log(response);
                        navigate("/");
                    })
                    .catch((err) => {
                        console.log(err.response.data.err.errors);
                        setErrors(err.response.data.err.errors);
                    });
        };

    return (
        <div className="container">
            <div className="row">
                <Navbar style={{justifyContent:"space-between"}}>
                    <h1>Wizard of Odds</h1>
                    <Link to="/">back to home</Link>
                </Navbar>
                <h3>Add a new deck!</h3>
                    <form onSubmit={handleSubmit}>
                    <div className="row">
                        <div className="col-4">
                        <div className="form-group">
                            <label htmlFor="name">Deck Name:</label>
                            <input
                                type="text"
                                className="form-control"
                                onChange={(e) => setName(e.target.value)}
                                value={name}
                            />
                            {errors.name ? <p>{errors.name.message}</p> : null}
                        </div>
                        <br></br>
                        <div className="form-group">
                            <label htmlFor="name">Colors:</label>
                            <input
                                type="text"
                                className="form-control"
                                onChange={(e) => setColors(e.target.value)}
                                value={colors}
                            />
                        </div>
                        <br></br>
                        <div className="card bordered">
                        <p>Magic: The Gathering has five colors: White, Blue, Black, Red, and Green. Please enter the colors that make up your deck. If your deck has all five colors, type "All". If your deck has no colors, type "Colorless". To choose multiple colors, seperate them with a "/". </p>
                        </div>
                    </div>
                    </div>
                        <button className="btn btn-primary" type="submit">
                            Add Deck
                        </button>
                    </form>
            </div>
        </div>
    );
};

export default DeckForm;
