import {useEffect, useState } from "react";
import axios from "axios";


const Calculator = () => {
    const [allDecks, setAllDecks] = useState([]);
    useEffect(()=> {
        axios
        .get("http://localhost:8000/api/deck")
            .then((res)=> {
                console.log(res.data);
                setAllDecks(res.data);
            })
            .catch((err)=> {
                console.log(err);
            });
    }, []);

    const calculate = (e) => {
        e.preventDefault();
        //var deck1 = document.getElementById("deck1");
        //var value1 = select.option[deck1.]
        //var deck2 = document.getElementById("deck2");
        
    }


    return (
        <div>
        <label htmlFor="deck1">Choose Player 1's Deck:</label>
        <select name="deck1" id="deck1">
            {allDecks.map((deck, index)=>{
                return (
                    <option key={deck._id}>{deck.name}: {deck.wins}-{deck.losses}</option>
                )
            })}
        </select>
        <p>Player 1 Win/Loss Record: {deck.wins}</p>
        <label htmlFor="deck2">Choose Player 2's Deck:</label>
        <select name="deck2" id="deck2">
            {allDecks.map((deck, index)=>{
                return (
                    <option key={deck._id}>{deck.name}: {deck.wins}-{deck.losses}</option>
                )
            })}
        </select>
        <button className="btn btn-primary" type="submit">
                            Calculate
                        </button>
        </div>
    );
};

export default Calculator;
